import java.io.BufferedReader;
import java.io.File;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.util.Map;

public class ShellUtils {
    private static final String[] BINARY_PLACES = {
            "/system/bin/",
            "/system/xbin/",
            "/sbin/",
            "/vendor/bin/",
            "/system/sbin/",
            "/data/local/"
    };
    private static String PYTHON_HOME;
    private static File DATA_DIR;
    private static File BUSYBOX_FILE;
    private static Boolean IS_ROOTED;

    public static Process executeNoWait(Map<String, String> env, String... command) throws IOException {
        ProcessBuilder pb = new ProcessBuilder(command);
        if (env != null) {
            pb.environment().putAll(env);
        }
        return pb.start();
    }

    public static Process sudoNoWait(Map<String, String> env, String... command) throws IOException {
        String[] sudoCommand = new String[command.length + 1];
        sudoCommand[0] = "su";
        System.arraycopy(command, 0, sudoCommand, 1, command.length);
        return executeNoWait(env, sudoCommand);
    }

    public static String waitFor(String command, Process process) throws IOException, InterruptedException {
        InputStream stdout = process.getInputStream();
        InputStream stderr = process.getErrorStream();
        BufferedReader stdoutReader = new BufferedReader(new InputStreamReader(stdout));
        BufferedReader stderrReader = new BufferedReader(new InputStreamReader(stderr));

        StringBuilder output = new StringBuilder();
        String line;
        while ((line = stdoutReader.readLine()) != null) {
            output.append(line).append("\n");
        }
        while ((line = stderrReader.readLine()) != null) {
            output.append(line).append("\n");
        }

        int exitCode = process.waitFor();
        if (exitCode != 0) {
            throw new IOException("Command '" + command + "' failed with exit code " + exitCode + "\n" + output);
        }
        return output.toString();
    }

    public static String findCommand(String command) {
        for (String path : BINARY_PLACES) {
            File file = new File(path + command);
            if (file.exists() && file.canExecute()) {
                return file.getAbsolutePath();
            }
        }
        return null;
    }
}

