        PowerManager.WakeLock wakeLock = pm.newWakeLock(PowerManager.PARTIAL_WAKE_LOCK, TAG);
        try {
            wakeLock.acquire();
            Process process = pb.start();

            Thread lInfo = null;
            Thread lWarn = null;
            if (returnStdOut) {
                BufferedReader br = new BufferedReader(new InputStreamReader(process.getInputStream(), Charsets.UTF_8));
                String line;
                while ((line = br.readLine()) != null) {
                    Log.println(Log.INFO, TAG, line);
                    capturedStdOut = capturedStdOut + line + "\n";
                }
                br.close();
            } else {
                lInfo = log(process.getInputStream(), Log.INFO);
                lWarn = log(process.getErrorStream(), Log.WARN);
            }

            int ret = process.waitFor();
            Log.i(TAG, "Process exited with code " + ret);
            if (lInfo != null) lInfo.join();
            if (lWarn != null) lWarn.join();
        } catch (IOException | InterruptedException e) {
            Log.e(TAG, "Failed to execute process or read output", e);
        } finally {
            wakeLock.release();
        }
        return capturedStdOut;
    }

    public List<String> getProcessPIDs(String processName) {
        List<String> pids = new ArrayList<>();
