import redis.clients.jedis.Jedis;
import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPubSub;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.ConcurrentMap;

public class RedisRegistry {
    private final ConcurrentHashMap<String, JedisPool> jedisPools = new ConcurrentHashMap<>();
    private final String clusterName;
    private final boolean replicate;
    private final ConcurrentMap<String, Notifier> notifiers = new ConcurrentHashMap<>();

    public RedisRegistry(AppContext appContext) {
        this.clusterName = appContext.getClusterName();
        this.replicate = appContext.isReplicate();
        for (String address : appContext.getRedisAddresses()) {
            jedisPools.put(address, new JedisPool(address));
        }
    }

    protected void doRegister(Node node) throws NodeRegistryException {
        try {
            for (JedisPool pool : jedisPools.values()) {
                try (Jedis jedis = pool.getResource()) {
                    jedis.set(node.getPath(), node.getData());
                    if (replicate) {
                        jedis.publish(node.getPath(), "REGISTER:" + node.getData());
                    }
                }
            }
        } catch (Exception e) {
            throw new NodeRegistryException("Failed to register node", e);
        }
    }

    protected void doSubscribe(Node node, NotifyListener listener) {
        Notifier notifier = notifiers.computeIfAbsent(node.getPath(), k -> new Notifier(node.getPath(), listener));
        notifier.start();
    }

    private class NotifySub extends JedisPubSub {
        private final NotifyListener listener;

        NotifySub(NotifyListener listener) {
            this.listener = listener;
        }

        @Override
        public void onMessage(String key, String msg) {
            if (msg.startsWith("REGISTER:")) {
                listener.notify(new Node(key, msg.substring(9)));
            }
        }
    }

    private class Notifier extends Thread {
        private final String path;
        private final NotifyListener listener;
        private volatile boolean running = true;
        private NotifySub sub;

        Notifier(String path, NotifyListener listener) {
            this.path = path;
            this.listener = listener;
            this.sub = new NotifySub(listener);
        }

        @Override
        public void run() {
            while (running) {
                try {
                    JedisPool pool = jedisPools.values().iterator().next();
                    try (Jedis jedis = pool.getResource()) {
                        jedis.subscribe(sub, path);
                    }
                } catch (Exception e) {
                    if (running) {
                        try {
                            Thread.sleep(1000);
                        } catch (InterruptedException ignored) {}
                    }
                }
            }
        }

        public void shutdown() {
            running = false;
            if (sub != null) {
                sub.unsubscribe();
            }
        }
    }
}

