    public void initializeSlotsCache(Set<HostAndPort> startNodes, JedisClientConfig clientConfig) {
        for (HostAndPort hostAndPort : startNodes) {
            try {
                cache.discoverClusterNodesAndSlots(hostAndPort, clientConfig);
                return;
            } catch (JedisConnectionException e) {
