        }
        return getConnectionFromSlot(JedisClusterCRC16.getSlot(args.getFirstKey()));
    }

    public Connection getConnection() {
        return cache.getRandomConnection();
    }

    public Connection getConnectionFromSlot(int slot) {
        Connection connection = cache.getSlotPool(slot).getResource();
        if (connection == null) {
            renewSlotCache();
            connection = cache.getSlotPool(slot).getResource();
            if (connection == null) {
                throw new JedisClusterOperationException("No reachable node for slot " + slot);
            }
        }
        return connection;
    }

    public void renewSlotCache() {
        cache.renewClusterSlots();
    }

    public void close() {
        cache.reset();
    }

    public Map<String, ConnectionPool> getConnectionMap() {
        return Collections.unmodifiableMap(cache.getNodes());
    }
}

