import redis.clients.jedis.JedisPubSub;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import java.util.logging.Logger;

public class RedisPubSubManager {
    private JedisPool jedisPool;
    private String channelName;
    private Logger logger;
    private ExecutorService subscriberExecutor;

    public RedisPubSubManager(String host, int port, String channelName) {
        this.channelName = channelName;
        this.logger = Logger.getLogger(RedisPubSubManager.class.getName());
        JedisPoolConfig poolConfig = new JedisPoolConfig();
        poolConfig.setMaxTotal(10);
        poolConfig.setMaxIdle(5);
        poolConfig.setMinIdle(1);
        this.jedisPool = new JedisPool(poolConfig, host, port);
        this.subscriberExecutor = Executors.newSingleThreadExecutor();
    }

    public void startSubscriber(JedisPubSub subscriber) {
        subscriberExecutor.submit(() -> {
            try (Jedis jedis = jedisPool.getResource()) {
                jedis.subscribe(subscriber, channelName);
            } catch (Exception e) {
                logger.severe("Subscriber error: " + e.getMessage());
            }
        });
    }

    public void startPublisher() {
        try (Jedis jedis = jedisPool.getResource()) {
            jedis.publish(channelName, "Publisher started");
        } catch (Exception e) {
            logger.severe("Publisher error: " + e.getMessage());
        }
    }

    public void publishMessage(String message) {
        try (Jedis jedis = jedisPool.getResource()) {
            jedis.publish(channelName, message);
        } catch (Exception e) {
            logger.severe("Publish error: " + e.getMessage());
        }
    }

    public void shutdown() {
        if (subscriberExecutor != null) {
            subscriberExecutor.shutdownNow();
        }
        if (jedisPool != null) {
            jedisPool.close();
        }
    }
}

