                        String[] parts = server.split(":");
                        String host = parts[0];
                        int port = Integer.parseInt(parts[1]);
                        shards.add(new JedisShardInfo(host, port));
                    }
                    shardedJedisPool = new ShardedJedisPool(new JedisPoolConfig(), shards);
                }
            } finally {
                INSTANCE_INIT_LOCK.unlock();
            }
