import java.nio.ByteBuffer;
import java.nio.ByteOrder;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import javax.sound.sampled.AudioFormat;

public class WaveWriter implements AutoCloseable {
    private final AudioFormat mAudioFormat;
    private int mFileRolloverCounter = 0;
    private final long mMaxSize;
    private Path mFile;
    private FileChannel mFileChannel;
    private boolean mDataChunkOpen = false;
    private long mDataChunkSizeOffset;
    private int mDataChunkSize = 0;

    public WaveWriter(AudioFormat format, Path file, long maxSize) {
        this.mAudioFormat = format;
        this.mFile = file;
        this.mMaxSize = maxSize;
        open();
    }

    public WaveWriter(AudioFormat format, Path file) {
        this(format, file, 0);
    }

    private void open() {
        try {
            mFileChannel = FileChannel.open(mFile, StandardOpenOption.CREATE, StandardOpenOption.WRITE, StandardOpenOption.TRUNCATE_EXISTING);
            ByteBuffer header = getWaveHeader(mAudioFormat);
            mFileChannel.write(header);
            openDataChunk();
        } catch (Exception e) {
            throw new RuntimeException("Failed to open WAV file", e);
        }
    }

    @Override
    public void close() {
        try {
            if (mFileChannel != null && mFileChannel.isOpen()) {
                closeDataChunk();
                updateTotalSize();
                mFileChannel.close();
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to close WAV file", e);
        }
    }

    public void close(Path path) {
        try {
            if (mFileChannel != null && mFileChannel.isOpen()) {
                closeDataChunk();
                updateTotalSize();
                mFileChannel.close();
                mFile.toFile().renameTo(path.toFile());
            }
        } catch (Exception e) {
            throw new RuntimeException("Failed to close and rename WAV file", e);
        }
    }

    public void writeData(ByteBuffer buffer) {
        try {
            if (mMaxSize > 0 && mFileChannel.position() + buffer.remaining() > mMaxSize) {
                rollover();
            }
            if (!mDataChunkOpen) {
                openDataChunk();
            }
            int bytesWritten = mFileChannel.write(buffer);
            mDataChunkSize += bytesWritten;
        } catch (Exception e) {
            throw new RuntimeException("Failed to write audio data", e);
        }
    }

    private void closeDataChunk() {
        if (mDataChunkOpen) {
            updateDataChunkSize();
            mDataChunkOpen = false;
        }
    }

    private void openDataChunk() {
        try {
            ByteBuffer dataHeader = getDataHeader();
            mDataChunkSizeOffset = mFileChannel.position();
            mFileChannel.write(dataHeader);
            mDataChunkSize = 0;
            mDataChunkOpen = true;
        } catch (Exception e) {
            throw new RuntimeException("Failed to open data chunk", e);
        }
    }

    private void rollover() {
        close();
        updateFileName();
        open();
    }

    private void updateTotalSize() {
        try {
            long fileSize = mFileChannel.size() - 8;
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(fileSize);
            mFileChannel.position(4);
            mFileChannel.write(sizeBuffer);
        } catch (Exception e) {
            throw new RuntimeException("Failed to update WAV file size", e);
        }
    }

    private void updateDataChunkSize() {
        try {
            ByteBuffer sizeBuffer = getUnsignedIntegerBuffer(mDataChunkSize);
            mFileChannel.position(mDataChunkSizeOffset + 4);
            mFileChannel.write(sizeBuffer);
            mFileChannel.position(mFileChannel.size());
        } catch (Exception e) {
            throw new RuntimeException("Failed to update data chunk size", e);
        }
    }

    protected static ByteBuffer getUnsignedIntegerBuffer(long size) {
        ByteBuffer buffer = ByteBuffer.allocate(4).order(ByteOrder.LITTLE_ENDIAN);
        buffer.putInt((int) size);
        buffer.flip();
        return buffer;
    }

    private void updateFileName() {
        String baseName = mFile.getFileName().toString().replaceFirst("[.][^.]+$", "");
        String extension = ".wav";
        mFile = mFile.resolveSibling(baseName + "_" + (++mFileRolloverCounter) + extension);
    }

    public static ByteBuffer getDataHeader() {
        ByteBuffer buffer = ByteBuffer.allocate(8).order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("data".getBytes());
        buffer.putInt(0);
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getWaveHeader(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(44).order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("RIFF".getBytes());
        buffer.putInt(0);
        buffer.put("WAVE".getBytes());
        buffer.put(getFormatChunk(format));
        buffer.put(getDataHeader());
        buffer.flip();
        return buffer;
    }

    public static ByteBuffer getFormatChunk(AudioFormat format) {
        ByteBuffer buffer = ByteBuffer.allocate(24).order(ByteOrder.LITTLE_ENDIAN);
        buffer.put("fmt ".getBytes());
        buffer.putInt(16);
        buffer.putShort((short) 1);
        buffer.putShort((short) format.getChannels());
        buffer.putInt((int) format.getSampleRate());
        buffer.putInt((int) (format.getSampleRate() * format.getFrameSize()));
        buffer.putShort((short) format.getFrameSize());
        buffer.putShort((short) format.getSampleSizeInBits());
        buffer.flip();
        return buffer;
    }
}

