import redis.clients.jedis.JedisPool;
import redis.clients.jedis.JedisPoolConfig;
import redis.clients.jedis.exceptions.JedisException;

public class RedisDistributeLock {
    private static JedisPool pool;
    private JedisLock jedisLock;
    private String lockKey;
    private Jedis jedis;
    private int timeoutMsecs;
    private int expireMsecs;

    public RedisDistributeLock(String lockKey) {
        this(lockKey, 3000, 300000);
    }

    public RedisDistributeLock(String lockKey, int timeoutMsecs, int expireMsecs) {
        this.lockKey = lockKey;
        this.timeoutMsecs = timeoutMsecs;
        this.expireMsecs = expireMsecs;
    }

    public void wrap(Runnable runnable) {
        try {
            jedis = pool.getResource();
            jedisLock = new JedisLock(jedis, lockKey, timeoutMsecs, expireMsecs);
            if (jedisLock.acquire()) {
                try {
                    runnable.run();
                } finally {
                    lockRelease(jedisLock, jedis);
                }
            }
        } catch (JedisException e) {
            if (jedis != null) {
                jedis.close();
            }
            throw e;
        }
    }

    private void lockRelease(JedisLock lock, Jedis jedis) {
        if (lock != null) {
            lock.release();
        }
        if (jedis != null) {
            jedis.close();
        }
    }

    public static JedisPool getPool() {
        return pool;
    }

    public static void setPool(JedisPool pool) {
        RedisDistributeLock.pool = pool;
    }

    private static class JedisLock {
        private Jedis jedis;
        private String lockKey;
        private int timeoutMsecs;
        private int expireMsecs;
        private boolean locked = false;

        public JedisLock(Jedis jedis, String lockKey, int timeoutMsecs, int expireMsecs) {
            this.jedis = jedis;
            this.lockKey = lockKey;
            this.timeoutMsecs = timeoutMsecs;
            this.expireMsecs = expireMsecs;
        }

        public synchronized boolean acquire() throws InterruptedException {
            int timeout = timeoutMsecs;
            while (timeout >= 0) {
                long expires = System.currentTimeMillis() + expireMsecs + 1;
                String expiresStr = String.valueOf(expires);
                if (jedis.setnx(lockKey, expiresStr) == 1) {
                    locked = true;
                    return true;
                }
                String currentValueStr = jedis.get(lockKey);
                if (currentValueStr != null && Long.parseLong(currentValueStr) < System.currentTimeMillis()) {
                    String oldValueStr = jedis.getSet(lockKey, expiresStr);
                    if (oldValueStr != null && oldValueStr.equals(currentValueStr)) {
                        locked = true;
                        return true;
                    }
                }
                timeout -= 100;
                Thread.sleep(100);
            }
            return false;
        }

        public synchronized void release() {
            if (locked) {
                jedis.del(lockKey);
                locked = false;
            }
        }
    }
}

