import redis.clients.jedis.JedisSentinelPool;
import redis.clients.jedis.exceptions.JedisConnectionException;
import java.util.Set;
import java.util.concurrent.atomic.AtomicBoolean;

public class SentineledConnectionProvider {
    private volatile HostAndPort currentMaster;
    private volatile ConnectionPool pool;
    private final String masterName;
    private final JedisClientConfig masterClientConfig;
    private final JedisClientConfig sentinelClientConfig;
    private final Set<HostAndPort> sentinels;
    private final SentinelListener sentinelListener;
    private final AtomicBoolean running = new AtomicBoolean(false);

    public SentineledConnectionProvider(String masterName, JedisClientConfig masterClientConfig,
                                      Set<HostAndPort> sentinels, JedisClientConfig sentinelClientConfig) {
        this.masterName = masterName;
        this.masterClientConfig = masterClientConfig;
        this.sentinels = sentinels;
        this.sentinelClientConfig = sentinelClientConfig;
        this.currentMaster = initSentinels(sentinels);
        this.pool = new ConnectionPool(currentMaster, masterClientConfig);
        this.sentinelListener = new SentinelListener(currentMaster);
        this.running.set(true);
        this.sentinelListener.start();
    }

    public Connection getConnection() {
        return pool.getResource();
    }

    private synchronized void initMaster(HostAndPort master) {
        if (master.equals(currentMaster)) {
            return;
        }
        ConnectionPool oldPool = pool;
        currentMaster = master;
        pool = new ConnectionPool(currentMaster, masterClientConfig);
        if (oldPool != null) {
            oldPool.close();
        }
    }

    private HostAndPort initSentinels(Set<HostAndPort> sentinels) {
        for (HostAndPort sentinel : sentinels) {
            try (Connection connection = new Connection(sentinel, sentinelClientConfig)) {
                String masterHost = connection.execute("SENTINEL", "get-master-addr-by-name", masterName).get(0);
                int masterPort = Integer.parseInt(connection.execute("SENTINEL", "get-master-addr-by-name", masterName).get(1));
                return new HostAndPort(masterHost, masterPort);
            } catch (JedisConnectionException e) {
                continue;
            }
        }
        throw new JedisConnectionException("All sentinels down, cannot determine master");
    }

    private class SentinelListener extends Thread {
        private final HostAndPort node;

        public SentinelListener(HostAndPort node) {
            this.node = node;
            setDaemon(true);
        }

        @Override
        public void run() {
            while (running.get()) {
                try (Connection connection = new Connection(node, sentinelClientConfig)) {
                    connection.subscribe(new Connection.PubSub() {
                        @Override
                        public void onMessage(String channel, String message) {
                            if (message.contains("+switch-master")) {
                                String[] parts = message.split(" ");
                                if (parts.length > 3 && masterName.equals(parts[0])) {
                                    String newMasterHost = parts[3];
                                    int newMasterPort = Integer.parseInt(parts[4]);
                                    initMaster(new HostAndPort(newMasterHost, newMasterPort));
                                }
                            }
                        }
                    }, "+switch-master");
                } catch (JedisConnectionException e) {
                    try {
                        Thread.sleep(1000);
                    } catch (InterruptedException ie) {
                        Thread.currentThread().interrupt();
                    }
                }
            }
        }
    }

    public void shutdown() {
        running.set(false);
        sentinelListener.interrupt();
        if (pool != null) {
            pool.close();
        }
    }
}

