import java.io.IOException;
import java.nio.MappedByteBuffer;
import java.nio.channels.FileChannel;
import java.nio.file.Path;
import java.nio.file.StandardOpenOption;
import sun.misc.Unsafe;
import java.lang.reflect.Field;

public class ArrayLongLongMapMmap {
    private static final int SEGMENT_SIZE = 1 << 27;
    private static final Unsafe UNSAFE;
    static {
        try {
            Field f = Unsafe.class.getDeclaredField("theUnsafe");
            f.setAccessible(true);
            UNSAFE = (Unsafe) f.get(null);
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    private final Path path;
    private FileChannel writeChannel;
    private MappedByteBuffer[] segmentsArray;
    private FileChannel readChannel;

    public ArrayLongLongMapMmap(Path path, boolean madvise) throws IOException {
        this.path = path;
        this.writeChannel = FileChannel.open(path, StandardOpenOption.READ, StandardOpenOption.WRITE, StandardOpenOption.CREATE);
    }

    public void init() throws IOException {
        if (writeChannel == null) return;
        long size = writeChannel.size();
        int segments = (int) ((size + SEGMENT_SIZE - 1) / SEGMENT_SIZE);
        segmentsArray = new MappedByteBuffer[segments];
        for (int i = 0; i < segments; i++) {
            long position = (long) i * SEGMENT_SIZE;
            long remaining = Math.min(SEGMENT_SIZE, size - position);
            segmentsArray[i] = writeChannel.map(FileChannel.MapMode.READ_ONLY, position, remaining);
        }
        writeChannel.close();
        writeChannel = null;
        readChannel = FileChannel.open(path, StandardOpenOption.READ);
    }

    public long get(long key) {
        int segmentIndex = (int) (key >>> 27);
        int offset = (int) (key & (SEGMENT_SIZE - 1));
        MappedByteBuffer segment = segmentsArray[segmentIndex];
        return segment.getLong(offset << 3);
    }

    public void close() throws IOException {
        if (segmentsArray != null) {
            for (MappedByteBuffer buffer : segmentsArray) {
                if (buffer != null) {
                    UNSAFE.invokeCleaner(buffer);
                }
            }
            segmentsArray = null;
        }
        if (writeChannel != null) {
            writeChannel.close();
            writeChannel = null;
        }
        if (readChannel != null) {
            readChannel.close();
            readChannel = null;
        }
        if (path != null) {
            path.toFile().delete();
        }
    }
}

