import org.apache.flink.configuration.Configuration;
import org.apache.flink.streaming.api.functions.sink.RichSinkFunction;
import org.apache.rocketmq.client.exception.MQClientException;
import org.apache.rocketmq.client.producer.DefaultMQProducer;
import org.apache.rocketmq.client.producer.SendCallback;
import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;

import java.util.ArrayList;
import java.util.List;
import java.util.Properties;

public class RocketMQSink<IN> extends RichSinkFunction<IN> {
    private static final Logger LOG = LoggerFactory.getLogger(RocketMQSink.class);
    private transient DefaultMQProducer producer;
    private final boolean async;
    private final Properties props;
    private final TopicSelector<IN> topicSelector;
    private final KeyValueSerializationSchema<IN> serializationSchema;
    private final boolean batchFlushOnCheckpoint;
    private final int batchSize;
    private transient List<Message> batchList;
    private final int messageDeliveryDelayLevel;

    public RocketMQSink(KeyValueSerializationSchema<IN> serializationSchema, TopicSelector<IN> topicSelector, Properties props) {
        this.serializationSchema = serializationSchema;
        this.topicSelector = topicSelector;
        this.props = props;
        this.async = Boolean.parseBoolean(props.getProperty("async", "false"));
        this.batchFlushOnCheckpoint = Boolean.parseBoolean(props.getProperty("batchFlushOnCheckpoint", "false"));
        this.batchSize = Integer.parseInt(props.getProperty("batchSize", "100"));
        this.messageDeliveryDelayLevel = Integer.parseInt(props.getProperty("messageDeliveryDelayLevel", "0"));
    }

    @Override
    public void open(Configuration parameters) throws Exception {
        producer = new DefaultMQProducer(props.getProperty("producerGroup", "defaultGroup"));
        producer.setNamesrvAddr(props.getProperty("namesrvAddr", "localhost:9876"));
        producer.setRetryTimesWhenSendFailed(Integer.parseInt(props.getProperty("retryTimes", "3")));
        producer.setSendMsgTimeout(Integer.parseInt(props.getProperty("sendTimeout", "3000")));
        try {
            producer.start();
        } catch (MQClientException e) {
            LOG.error("Failed to start RocketMQ producer", e);
            throw new RuntimeException(e);
        }
        if (batchFlushOnCheckpoint) {
            batchList = new ArrayList<>(batchSize);
        }
    }

    @Override
    public void invoke(IN value, Context context) throws Exception {
        Message message = prepareMessage(value);
        if (batchFlushOnCheckpoint) {
            batchList.add(message);
            if (batchList.size() >= batchSize) {
                flushSync();
            }
        } else {
            sendMessage(message);
        }
    }

    private Message prepareMessage(IN value) {
        byte[] body = serializationSchema.serializeValue(value);
        String topic = topicSelector.getTopic(value);
        String tags = topicSelector.getTag(value);
        Message message = new Message(topic, tags, body);
        if (messageDeliveryDelayLevel > 0) {
            message.setDelayTimeLevel(messageDeliveryDelayLevel);
        }
        return message;
    }

    private void sendMessage(Message message) throws Exception {
        if (async) {
            producer.send(message, new SendCallback() {
                @Override
                public void onSuccess(SendResult sendResult) {
                    LOG.debug("Message sent successfully: {}", sendResult);
                }
                @Override
                public void onException(Throwable e) {
                    LOG.error("Failed to send message asynchronously", e);
                }
            });
        } else {
            SendResult result = producer.send(message);
            LOG.debug("Message sent synchronously: {}", result);
        }
    }

    public void flushSync() throws Exception {
        if (batchList != null && !batchList.isEmpty()) {
            producer.send(batchList);
            batchList.clear();
        }
    }

    @Override
    public void close() throws Exception {
        if (producer != null) {
            if (batchFlushOnCheckpoint && batchList != null && !batchList.isEmpty()) {
                flushSync();
            }
            producer.shutdown();
        }
    }
}

