import org.apache.rocketmq.client.producer.SendResult;
import org.apache.rocketmq.common.message.Message;

public class AsyncProducer {
    private DefaultMQProducer producer;

    public AsyncProducer(String producerGroup, String namesrvAddr) throws Exception {
        this.producer = new DefaultMQProducer(producerGroup);
        this.producer.setNamesrvAddr(namesrvAddr);
        this.producer.setRetryTimesWhenSendAsyncFailed(0);
        this.producer.start();
    }

    public void sendAsyncMessage(String topic, String tags, String keys, String body) throws Exception {
        Message message = new Message(topic, tags, keys, body.getBytes());
        this.producer.send(message, new SendCallback() {
            @Override
            public void onSuccess(SendResult sendResult) {
                System.out.println("Message sent successfully: " + sendResult);
            }

            @Override
            public void onException(Throwable e) {
                System.err.println("Failed to send message: " + e.getMessage());
            }
        });
    }

    public void shutdown() {
        this.producer.shutdown();
    }
}

