import java.io.*;
import java.net.*;
import org.ietf.jgss.*;

public class GSSClient {
    private Socket s;

    public static void main(String[] args) throws Exception {
        if (args.length < 3) {
            usage();
            return;
        }

        String serverName = args[0];
        int port = Integer.parseInt(args[1]);
        String serviceName = args[2];

        GSSClient client = new GSSClient();
        client.s = new Socket(serverName, port);

        Oid krb5Oid = new Oid("1.2.840.113554.1.2.2");
        GSSContext context = client.createCtxt(serviceName, krb5Oid);

        BufferedReader stdin = new BufferedReader(new InputStreamReader(System.in));
        DataInputStream in = new DataInputStream(client.s.getInputStream());
        DataOutputStream out = new DataOutputStream(client.s.getOutputStream());

        while (true) {
            System.out.print("Enter message to send (or 'quit' to exit): ");
            String msg = stdin.readLine();
            if (msg.equalsIgnoreCase("quit")) {
                break;
            }
            client.sendMsgToPeer(context, msg);
            byte[] token = new byte[in.readInt()];
            in.readFully(token);
            String receivedMsg = new String(context.unwrap(token, 0, token.length).getMessage());
            client.verifyMsgFromPeer(context, receivedMsg);
        }

        context.dispose();
        client.exit(0);
    }

    private GSSContext createCtxt(String serverName, Oid mechOid) throws GSSException {
        GSSManager manager = GSSManager.getInstance();
        GSSName peerName = manager.createName(serverName, null);
        GSSContext context = manager.createContext(peerName, mechOid, null, GSSContext.DEFAULT_LIFETIME);
        context.requestMutualAuth(true);
        context.requestConf(true);
        context.requestInteg(true);

        byte[] token = new byte[0];
        while (!context.isEstablished()) {
            token = context.initSecContext(token, 0, token.length);
            if (token != null) {
                DataOutputStream out = new DataOutputStream(s.getOutputStream());
                out.writeInt(token.length);
                out.write(token);
                out.flush();

                DataInputStream in = new DataInputStream(s.getInputStream());
                token = new byte[in.readInt()];
                in.readFully(token);
            }
        }
        return context;
    }

    private void sendMsgToPeer(GSSContext aCtxt, String msg) throws Exception {
        byte[] message = msg.getBytes();
        byte[] token = aCtxt.wrap(message, 0, message.length, null);
        DataOutputStream out = new DataOutputStream(s.getOutputStream());
        out.writeInt(token.length);
        out.write(token);
        out.flush();
    }

    private void verifyMsgFromPeer(GSSContext aCtxt, String msg) throws GSSException {
        System.out.println("Received message: " + msg);
        byte[] mic = aCtxt.getMIC(msg.getBytes(), 0, msg.getBytes().length, null);
        aCtxt.verifyMIC(mic, 0, mic.length, msg.getBytes(), 0, msg.getBytes().length, null);
        System.out.println("Message verified successfully");
    }

    private void usage() {
        System.out.println("Usage: java GSSClient <server> <port> <serviceName>");
    }

    private void print(String msg) {
        System.out.println(msg);
    }

    private void exit(int status) {
        try {
            if (s != null) {
                s.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
        System.exit(status);
    }
}

