import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.util.Map;

public class HttpCommunicator {

    private HttpClient httpClient;

    public HttpCommunicator() {
        this.httpClient = HttpClient.newBuilder()
                .followRedirects(HttpClient.Redirect.ALWAYS)
                .build();
    }

    public String sendGetRequest(String uri, Map<String, Object> attributes) {
        boolean isOptional = attributes.containsKey("optional-option");
        return readUri(URI.create(uri), isOptional);
    }

    private String readUri(URI uri, boolean optional) {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(uri)
                .GET()
                .build();
        try {
            return httpClient.send(request, HttpResponse.BodyHandlers.ofString()).body();
        } catch (Exception e) {
            if (optional) {
                return "";
            } else {
                throw new RuntimeException(e);
            }
        }
    }

    private boolean isHttpOrHttps(String url) {
        return url != null && (url.startsWith("http://") || url.startsWith("https://"));
    }
}

