import java.io.*;
import java.net.*;
import javax.servlet.*;
import javax.servlet.http.*;

public class Lisplet extends HttpServlet {
    private String host;
    private int port;

    @Override
    protected void doPost(HttpServletRequest req, HttpServletResponse resp) throws ServletException, IOException {
        Socket socket = null;
        try {
            socket = new Socket(host, port);
            OutputStream out = socket.getOutputStream();
            Writer writer = new OutputStreamWriter(out);
            sendRequest(req, writer);
            writer.flush();

            PushbackInputStream in = new PushbackInputStream(socket.getInputStream());
            if (getResponseHeader(req, resp, in)) {
                transferResponseBody(resp, in);
            }
        } finally {
            if (socket != null) {
                socket.close();
            }
        }
    }

    private void transferResponseBody(HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        OutputStream out = resp.getOutputStream();
        byte[] buffer = new byte[4096];
        int bytesRead;
        while ((bytesRead = strm.read(buffer)) != -1) {
            out.write(buffer, 0, bytesRead);
        }
    }

    private boolean getResponseHeader(HttpServletRequest req, HttpServletResponse resp, PushbackInputStream strm) throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(strm));
        String line;
        while ((line = reader.readLine()) != null) {
            if (line.isEmpty()) {
                break;
            }
            String[] parts = line.split(":", 2);
            if (parts.length == 2) {
                resp.setHeader(parts[0].trim(), parts[1].trim());
            }
        }
        return true;
    }

    private void sendRequest(HttpServletRequest req, Writer writer) throws IOException {
        writeTagValuePair(writer, "Method", req.getMethod());
        writeTagValuePair(writer, "Path", req.getRequestURI());
        writeTagValuePair(writer, "Protocol", req.getProtocol());

        for (String headerName : req.getHeaderNames()) {
            writeTagValuePair(writer, headerName, req.getHeader(headerName));
        }

        writer.write("\r\n");

        BufferedReader reader = req.getReader();
        char[] buffer = new char[4096];
        int charsRead;
        while ((charsRead = reader.read(buffer)) != -1) {
            writer.write(buffer, 0, charsRead);
        }
    }

    private void writeTagValuePair(Writer writer, String tag, String value) throws IOException {
        writer.write(tag);
        writer.write(": ");
        writer.write(value);
        writer.write("\r\n");
    }
}

