import java.io.*;
import java.net.Socket;
import java.util.ArrayList;
import java.util.List;

public class BinaryLinkClient {
    private final Object lock = new Object();
    private Socket clientSocket;
    private ObjectOutputStream oos;
    private ObjectInputStream ois;
    private ForwardingPrintStream streamErr;
    private ForwardingPrintStream streamOut;
    private OutputFormat outputFormat;
    private boolean failed;
    private int resetToGo;
    private List<Serializable> delayedFrames = new ArrayList<>();
    private boolean inFrame;

    public BinaryLinkClient(String hostName, int hostPort) throws IOException {
        clientSocket = new Socket(hostName, hostPort);
        oos = new ObjectOutputStream(clientSocket.getOutputStream());
        ois = new ObjectInputStream(clientSocket.getInputStream());
        streamOut = new ForwardingPrintStream(this, false);
        streamErr = new ForwardingPrintStream(this, true);
        outputFormat = new OutputFormat(this);
    }

    public void pushFrame(Serializable frame) {
        synchronized (lock) {
            if (failed) return;
            if (inFrame) {
                delayedFrames.add(frame);
                return;
            }
            try {
                inFrame = true;
                oos.writeObject(frame);
                oos.flush();
                if (--resetToGo <= 0) {
                    oos.reset();
                    resetToGo = 100;
                }
                inFrame = false;
                if (!delayedFrames.isEmpty()) {
                    List<Serializable> frames = new ArrayList<>(delayedFrames);
                    delayedFrames.clear();
                    for (Serializable f : frames) {
                        pushFrame(f);
                    }
                }
            } catch (IOException e) {
                failed = true;
                close();
                throw new RuntimeException("Failed to push frame", e);
            }
        }
    }

    public Object readFrame() {
        synchronized (lock) {
            if (failed) return null;
            try {
                return ois.readObject();
            } catch (IOException | ClassNotFoundException e) {
                failed = true;
                close();
                throw new RuntimeException("Failed to read frame", e);
            }
        }
    }

    public void close() {
        synchronized (lock) {
            if (clientSocket == null) return;
            try {
                clientSocket.close();
            } catch (IOException e) {
                throw new RuntimeException("Failed to close socket", e);
            } finally {
                clientSocket = null;
                oos = null;
                ois = null;
            }
        }
    }

    public Options handshake() {
        pushFrame(new HandshakeFrame());
        return (Options) readFrame();
    }

    public ActionPlan requestPlan() {
        pushFrame(new PlanRequestFrame());
        return (ActionPlan) readFrame();
    }

    public void pushResults(IterationResult res) {
        pushFrame(res);
    }

    public void pushException(BenchmarkException error) {
        pushFrame(new ExceptionFrame(error));
    }

    public void pushResultMetadata(BenchmarkResultMetaData res) {
        pushFrame(res);
    }

    public PrintStream getOutStream() {
        return streamOut;
    }

    public PrintStream getErrStream() {
        return streamErr;
    }

    public OutputFormat getOutputFormat() {
        return outputFormat;
    }

    public class ForwardingPrintStream extends PrintStream {
        private final BinaryLinkClient client;
        private final boolean isError;

        public ForwardingPrintStream(BinaryLinkClient client, boolean isError) {
            super(new ByteArrayOutputStream());
            this.client = client;
            this.isError = isError;
        }

        @Override
        public void write(byte[] buf, int off, int len) {
            client.pushFrame(new OutputFrame(buf, off, len, isError));
        }

        @Override
        public void flush() {
        }

        @Override
        public void close() {
        }
    }
