        this.mServerIpAddress = serverIpAddress;
        this.mPort = port;
    }

    public void setOnSendListener(OnSendListener listener) {
        this.mOnSendListener = listener;
    }

    @Override
    public void run() {
        try {
            if (mOnSendListener != null) {
                mOnSendListener.onStart();
            }
            init();
            parseHeader();
            parseBody();
            if (mOnSendListener != null) {
                mOnSendListener.onSuccess(mFileInfo);
            }
        } catch (Exception e) {
            if (mOnSendListener != null) {
                mOnSendListener.onFailure(e, mFileInfo);
            }
        } finally {
            finish();
        }
    }

    private void init() throws IOException {
        mSocket = new Socket(mServerIpAddress, mPort);
        mOutputStream = new BufferedOutputStream(mSocket.getOutputStream());
    }

    private void parseHeader() throws IOException {
        String header = String.format("%s|%d|%d\n", mFileInfo.getName(), mFileInfo.getSize(), mFileInfo.getLastModified());
        mOutputStream.write(header.getBytes());
        mOutputStream.flush();
    }

    private void parseBody() throws IOException {
        File file = new File(mFileInfo.getPath());
        FileInputStream fis = new FileInputStream(file);
        byte[] buffer = new byte[4096];
        int bytesRead;
        long totalBytesRead = 0;
        long fileSize = file.length();

        while ((bytesRead = fis.read(buffer)) != -1) {
            mOutputStream.write(buffer, 0, bytesRead);
            totalBytesRead += bytesRead;
            if (mOnSendListener != null) {
                mOnSendListener.onProgress(totalBytesRead, fileSize);
            }
        }
        fis.close();
        mOutputStream.flush();
    }

    private void finish() {
        try {
            if (mOutputStream != null) {
                mOutputStream.close();
            }
            if (mSocket != null) {
                mSocket.close();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public interface OnSendListener {
        void onStart();
        void onProgress(long bytesTransferred, long totalBytes);
        void onSuccess(FileInfo fileInfo);
        void onFailure(Throwable throwable, FileInfo fileInfo);
    }
}

