public class ProtobufIO {
    private Socket sock;
    private InputStream inputStream;
    private OutputStream outputStream;
    private Thread thread;
    private boolean shutdown;
    private PrintStream outStream;
    private final List<MessageCallback> callbacks = new CopyOnWriteArrayList<>();
    private final AtomicLong apiCallIdGen = new AtomicLong(0);
    private final Object CALLBACK_LOCK = new Object();

    public ProtobufIO(String host, int port, PrintStream out, String threadName) throws IOException {
        this.sock = new Socket(host, port);
        this.inputStream = sock.getInputStream();
        this.outputStream = sock.getOutputStream();
        this.outStream = out;
        this.thread = new Thread(new ProtobufIOWorker(), threadName);
        this.thread.start();
    }

    public long send(String apiCall, MessageLite msg) {
        long apiCallId = getNextApiCallId();
        send(apiCallId, apiCall, msg);
        return apiCallId;
    }

    public long send(long apiCallIdRef, String apiCallRef, MessageLite... messages) {
        try {
            ByteArrayOutputStream baos = new ByteArrayOutputStream();
            DataOutputStream dos = new DataOutputStream(baos);
            for (MessageLite msg : messages) {
                byte[] data = msg.toByteArray();
                dos.writeInt(data.length);
                dos.write(data);
            }
            send(baos.toByteArray());
            return apiCallIdRef;
        } catch (IOException e) {
            throw new RuntimeException("Failed to send message", e);
        }
    }

    public void send(byte[] data) {
        try {
            DataOutputStream dos = new DataOutputStream(outputStream);
            dos.writeInt(data.length);
            dos.write(data);
            dos.flush();
        } catch (IOException e) {
            throw new RuntimeException("Failed to send raw data", e);
        }
    }

    public void registerCallback(MessageCallback callback) {
        callbacks.add(callback);
    }

    public void unregisterCallback(MessageCallback callback) {
        callbacks.remove(callback);
    }

    public long getNextApiCallId() {
        return apiCallIdGen.incrementAndGet();
    }

    public void shutdown() {
        shutdown = true;
        thread.interrupt();
        try {
            if (sock != null) sock.close();
            if (inputStream != null) inputStream.close();
            if (outputStream != null) outputStream.close();
        } catch (IOException e) {
            outStream.println("Error during shutdown: " + e.getMessage());
        }
    }

    private void callback(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap) {
        synchronized (CALLBACK_LOCK) {
            for (MessageCallback cb : callbacks) {
                if (retCode >= 400) {
                    cb.onError(apiCallId, retCode, message, cause, correction, details, objRefsMap);
                } else if (retCode >= 300) {
                    cb.onWarning(apiCallId, retCode, message, cause, correction, details, objRefsMap);
                } else if (retCode >= 200) {
                    cb.onSuccess(apiCallId, retCode, message, objRefsMap);
                } else {
                    cb.onInfo(apiCallId, retCode, message, objRefsMap);
                }
            }
        }
    }

    private class ProtobufIOWorker implements Runnable {
        public void run() {
            DataInputStream dis = new DataInputStream(inputStream);
            while (!shutdown) {
                try {
                    int length = dis.readInt();
                    byte[] data = new byte[length];
                    dis.readFully(data);
                    // TODO: Parse protobuf message and extract fields for callback
                } catch (IOException e) {
                    if (!shutdown) {
                        outStream.println("Error reading message: " + e.getMessage());
                    }
                    break;
                }
            }
        }
    }

    public interface MessageCallback {
        void onError(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap);
        void onWarning(long apiCallId, long retCode, String message, String cause, String correction, String details, Map<String, String> objRefsMap);
        void onSuccess(long apiCallId, long retCode, String message, Map<String, String> objRefsMap);
        void onInfo(long apiCallId, long retCode, String message, Map<String, String> objRefsMap);
    }
}

