import java.net.InetSocketAddress;
import java.net.URI;
import java.net.http.HttpClient;
import java.net.http.HttpRequest;
import java.net.http.HttpResponse;
import java.time.Duration;
import java.util.ArrayList;
import java.util.List;
import java.util.concurrent.Executor;

public class HttpCommunicationClient {
    private InetSocketAddress address;
    private HttpClient httpClient;

    public HttpCommunicationClient(InetSocketAddress address) {
        this.address = address;
    }

    public void setupHttpClient(Executor threadPool) {
        this.httpClient = HttpClient.newBuilder()
                .followRedirects(HttpClient.Redirect.NORMAL)
                .connectTimeout(Duration.ofSeconds(10))
                .executor(threadPool)
                .build();
    }

    public void closeClient() {
        if (httpClient != null) {
            httpClient = null;
        }
    }

    private HttpResponse<String> executeAndDefaultAssertions(HttpRequest request) throws Exception {
        HttpResponse<String> response = httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        if (response.statusCode() >= 400) {
            throw new RuntimeException("HTTP error: " + response.statusCode());
        }
        return response;
    }

    public HttpResponse<String> get(String path, String[]... headers) throws Exception {
        HttpRequest.Builder builder = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostString() + ":" + address.getPort() + path))
                .GET();
        for (String[] header : headers) {
            if (header.length == 2) {
                builder.header(header[0], header[1]);
            }
        }
        return executeAndDefaultAssertions(builder.build());
    }

    public HttpResponse<String> browserGet(String uri) throws Exception {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(uri))
                .header("User-Agent", browserHeader()[1])
                .GET()
                .build();
        return executeAndDefaultAssertions(request);
    }

    public HttpResponse<String> post(String path) throws Exception {
        HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostString() + ":" + address.getPort() + path))
                .POST(HttpRequest.BodyPublishers.noBody())
                .build();
        return executeAndDefaultAssertions(request);
    }

    public List<String> getAllRedirectLocations(String path, String[]... headers) throws Exception {
        List<String> redirects = new ArrayList<>();
        HttpClient noRedirectClient = HttpClient.newBuilder()
                .followRedirects(HttpClient.Redirect.NEVER)
                .build();

        HttpRequest.Builder builder = HttpRequest.newBuilder()
                .uri(URI.create("http://" + address.getHostString() + ":" + address.getPort() + path))
                .GET();
        for (String[] header : headers) {
            if (header.length == 2) {
                builder.header(header[0], header[1]);
            }
        }

        HttpResponse<Void> response = noRedirectClient.send(builder.build(), HttpResponse.BodyHandlers.discarding());
        while (response.statusCode() >= 300 && response.statusCode() < 400) {
            String location = response.headers().firstValue("Location").orElse(null);
            if (location == null) break;
            redirects.add(location);
            builder.uri(URI.create(location));
            response = noRedirectClient.send(builder.build(), HttpResponse.BodyHandlers.discarding());
        }
        return redirects;
    }

    public static String[] browserHeader() {
        return new String[]{"User-Agent", "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/91.0.4472.124 Safari/537.36"};
    }
}

