    private boolean isConnected;
    private final int DEFAULT_READ_BUFFER_SIZE = 32768;
    private byte[] buffer;
    private byte[] receivedData;

    private SocketClient() {
        buffer = new byte[DEFAULT_READ_BUFFER_SIZE];
    }

    public static SocketClient getInstance() {
        if (socketClient == null) {
            socketClient = new SocketClient();
        }
        return socketClient;
    }

    public void openConnection(String ip, int port) throws Exception {
        if (isConnected) {
            closeConnection();
        }
        socket = new Socket(ip, port);
        outputStream = socket.getOutputStream();
        inputStream = socket.getInputStream();
        isConnected = true;
    }

    public boolean isConnected() {
        return isConnected;
    }

    public void write(byte[] data) throws Exception {
        if (!isConnected) {
            throw new Exception("Not connected");
        }
        outputStream.write(data);
        outputStream.flush();
    }

    public int read(int bytesToBeRead) throws Exception {
        if (!isConnected) {
            throw new Exception("Not connected");
        }
        int bytesRead = inputStream.read(buffer, 0, Math.min(bytesToBeRead, buffer.length));
        if (bytesRead > 0) {
            receivedData = new byte[bytesRead];
            System.arraycopy(buffer, 0, receivedData, 0, bytesRead);
        }
        return bytesRead;
    }

    public byte[] getReceivedData() {
        return receivedData;
    }

    public void closeConnection() {
        try {
            if (outputStream != null) {
                outputStream.close();
            }
            if (inputStream != null) {
                inputStream.close();
            }
            if (socket != null) {
                socket.close();
            }
