import java.io.IOException;
import java.net.InetSocketAddress;
import java.nio.channels.SelectionKey;
import java.nio.channels.Selector;
import java.nio.channels.SocketChannel;
import java.util.HashMap;
import java.util.Map;

public class NioTcpClient {
    private Selector nioSelector;
    private Map<String, SelectionKey> keyMap;
    private Time time;
    private SSLFactory sslFactory;
    private NetworkConfig networkConfig;

    public NioTcpClient(Time time, SSLFactory sslFactory, NetworkConfig networkConfig) throws IOException {
        this.nioSelector = Selector.open();
        this.keyMap = new HashMap<>();
        this.time = time;
        this.sslFactory = sslFactory;
        this.networkConfig = networkConfig;
    }

    public String connect(InetSocketAddress address, int readBufferSize, int writeBufferSize, PortType portType) throws IOException {
        SocketChannel channel = SocketChannel.open();
        channel.configureBlocking(false);
        channel.connect(address);

        SelectionKey key = channel.register(nioSelector, SelectionKey.OP_CONNECT);
        String connectionId = generateConnectionId(channel);
        key.attach(createTransmission(connectionId, key, address.toString(), readBufferSize, portType, SSLFactory.Mode.CLIENT));
        keyMap.put(connectionId, key);
        return connectionId;
    }

    private String generateConnectionId(SocketChannel channel) throws IOException {
        return channel.getLocalAddress().toString() + "-" + channel.getRemoteAddress().toString();
    }

    private Transmission createTransmission(String connectionId, SelectionKey key, String address, int bufferSize, PortType portType, SSLFactory.Mode mode) {
        if (portType == PortType.PLAINTEXT) {
            return new PlaintextTransmission(connectionId, key, address, bufferSize, time, networkConfig);
        } else {
            return sslFactory.create(connectionId, key, address, bufferSize, time, networkConfig, mode);
        }
    }

    public void disconnect(String connectionId) {
        SelectionKey key = keyMap.get(connectionId);
        if (key != null) {
            key.cancel();
            keyMap.remove(connectionId);
            try {
                key.channel().close();
            } catch (IOException e) {
            }
        }
    }

    public void close() {
        for (SelectionKey key : keyMap.values()) {
            key.cancel();
            try {
                key.channel().close();
            } catch (IOException e) {
            }
        }
        keyMap.clear();
        try {
            nioSelector.close();
        } catch (IOException e) {
        }
    }
}

