    }

    private class SocketHandler implements NIOEventHandler {
        private SocketChannel chan;
        private boolean awaitingWriteNotification = false;
        private ByteBuffer toWrite;
        private final ByteBuffer oblivion = ByteBuffer.allocateDirect(8192);

        @Override
        public void open() throws IOException {
            chan = SocketChannel.open();
            chan.configureBlocking(false);
            chan.connect(new InetSocketAddress(getAddress(), config.getServerPort()));
