        public void selectionEvent(SelectionKey key) {
            try {
                if (key.isConnectable()) {
                    if (chan.finishConnect()) {
                        key.interestOps(calcInterestOps());
                    }
                }
                if (key.isWritable()) {
                    write();
                }
                if (key.isReadable()) {
                    read();
                }
            } catch (IOException e) {
                try {
                    close();
                } catch (IOException ignored) {}
            }
        }

        private void write() throws IOException {
            if (toWrite == null) {
                toWrite = writeQueue.poll();
                if (toWrite == null) {
                    awaitingWriteNotification = false;
                    return;
                }
            }
            chan.write(toWrite);
            if (!toWrite.hasRemaining()) {
                toWrite = null;
            }
        }

        @Override
        public void read() throws IOException {
            oblivion.clear();
            chan.read(oblivion);
        }

        @Override
        public void doStateChecks(long now) {
            if (chan != null && !chan.isOpen()) {
                try {
                    close();
                } catch (IOException ignored) {}
            }
        }

        @Override
        public int calcInterestOps() {
            int ops = 0;
            if (chan.isConnectionPending()) {
                ops |= SelectionKey.OP_CONNECT;
            }
            ops |= SelectionKey.OP_READ;
            if (!writeQueue.isEmpty() || toWrite != null) {
                ops |= SelectionKey.OP_WRITE;
                awaitingWriteNotification = true;
            }
            return ops;
        }
    }
