        this.settings = settings;
        this.group = group;
    }

    public void openAsync(OperationContext operationContext, AsyncCompletionHandler<Void> handler) {
        Queue<SocketAddress> socketAddressQueue = inetAddressResolver.resolve(serverAddress);
        initializeSocketChannel(handler, socketAddressQueue);
    }

    private void initializeSocketChannel(AsyncCompletionHandler<Void> handler, Queue<SocketAddress> socketAddressQueue) {
        try {
            AsynchronousSocketChannel attemptConnectionChannel = group != null ? AsynchronousSocketChannel.open(group) : AsynchronousSocketChannel.open();
            configureSocketChannel(attemptConnectionChannel);
            SocketAddress socketAddress = socketAddressQueue.poll();
            attemptConnectionChannel.connect(socketAddress, null, new OpenCompletionHandler(handler, socketAddressQueue, attemptConnectionChannel));
        } catch (IOException e) {
            handler.failed(e, null);
        }
    }

    private void configureSocketChannel(AsynchronousSocketChannel channel) throws IOException {
        for (SocketOption<?> option : settings.getSocketOptions()) {
            channel.setOption(option, settings.getOptionValue(option));
