import java.io.*;
import java.net.*;
import java.nio.*;
import java.nio.channels.*;
import java.util.concurrent.*;

public class ClientController implements Runnable {
    private final BlockingQueue<Packet> inner = new LinkedBlockingQueue<>();
    private final Object lock = new Object();
    private final InetSocketAddress remote;
    private Thread thread;
    private SocketChannel channel;

    public ClientController(String host, int port) {
        this.remote = new InetSocketAddress(host, port);
    }

    public void start() {
        synchronized (lock) {
            if (thread == null) {
                thread = new Thread(this);
                thread.start();
            }
        }
    }

    public boolean put(Packet packet) {
        return inner.offer(packet);
    }

    public void clear() {
        inner.clear();
    }

    @Override
    public void run() {
        try {
            channel = SocketChannel.open();
            channel.configureBlocking(false);
            channel.connect(remote);

            while (!channel.finishConnect()) {
                Thread.sleep(10);
            }

            ByteBuffer buffer = ByteBuffer.allocate(1024);
            while (!Thread.interrupted()) {
                write();
                int bytesRead = channel.read(buffer);
                if (bytesRead > 0) {
                    buffer.flip();
                    buffer.clear();
                }
                Thread.sleep(10);
            }
        } catch (IOException | InterruptedException e) {
            e.printStackTrace();
        } finally {
            try {
                if (channel != null) {
                    channel.close();
                }
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void write() throws IOException {
        Packet packet = inner.poll();
        if (packet != null) {
            byte[] data = packet.getData();
            byte checksum = calculateChecksum(data);
            ByteBuffer buffer = ByteBuffer.allocate(data.length + 1);
            buffer.put(data);
            buffer.put(checksum);
            buffer.flip();
            while (buffer.hasRemaining()) {
                channel.write(buffer);
            }
        }
    }

    private byte calculateChecksum(byte[] data) {
        byte checksum = 0;
        for (byte b : data) {
            checksum ^= b;
        }
        return checksum;
    }
}

