import java.nio.channels.*;
import java.nio.file.*;
import java.io.IOException;
import java.util.concurrent.Executor;
import java.util.logging.Logger;

public class Server implements AutoCloseable {
    private final ServerSocketChannel serverSocketChannel;
    private final Path socketPath;
    private final Logger LOG = Logger.getLogger(Server.class.getName());

    private Server(ServerSocketChannel serverSocketChannel, Path socketPath) {
        this.serverSocketChannel = serverSocketChannel;
        this.socketPath = socketPath;
    }

    public static Server create(Path socketPath) throws IOException {
        ServerSocketChannel serverSocketChannel = ServerSocketChannel.open();
        serverSocketChannel.bind(UnixDomainSocketAddress.of(socketPath));
        return new Server(serverSocketChannel, socketPath);
    }

    public boolean isClient() {
        return false;
    }

    public void listen(IpcMessageListener listener, Executor executor) {
        executor.execute(() -> {
            try {
                while (serverSocketChannel.isOpen()) {
                    SocketChannel clientChannel = serverSocketChannel.accept();
                    if (clientChannel != null) {
                        executor.execute(() -> handleClient(clientChannel, listener));
                    }
                }
            } catch (IOException e) {
                LOG.severe("Error accepting client connection: " + e.getMessage());
            }
        });
    }

    private void handleClient(SocketChannel clientChannel, IpcMessageListener listener) {
        try {
            ByteBuffer buffer = ByteBuffer.allocate(1024);
            int bytesRead = clientChannel.read(buffer);
            if (bytesRead > 0) {
                buffer.flip();
                byte[] data = new byte[buffer.remaining()];
                buffer.get(data);
                listener.onMessage(new IpcMessage(data));
            }
        } catch (IOException e) {
            LOG.severe("Error reading from client: " + e.getMessage());
        } finally {
            try {
                clientChannel.close();
            } catch (IOException e) {
                LOG.severe("Error closing client channel: " + e.getMessage());
            }
        }
    }

    public void send(IpcMessage message, Executor executor) {
        executor.execute(() -> {
            try (SocketChannel clientChannel = serverSocketChannel.accept()) {
                ByteBuffer buffer = ByteBuffer.wrap(message.getData());
                while (buffer.hasRemaining()) {
                    clientChannel.write(buffer);
                }
            } catch (IOException e) {
                LOG.severe("Error sending message: " + e.getMessage());
            }
        });
    }

    @Override
    public void close() throws IOException {
        try {
            if (serverSocketChannel != null && serverSocketChannel.isOpen()) {
                serverSocketChannel.close();
            }
        } finally {
            Files.deleteIfExists(socketPath);
        }
    }
}

