import java.net.*;
import java.nio.ByteBuffer;
import java.nio.channels.DatagramChannel;
import java.security.SecureRandom;
import java.util.concurrent.LinkedTransferQueue;
import java.util.concurrent.TransferQueue;

public class OpentrackerLiveSync {
    private final TransferQueue<ByteBuffer> toSend = new LinkedTransferQueue<>();
    private DatagramChannel channel;
    private final byte[] id = new byte[4];
    private volatile boolean running;
    private static final int HEADER_LENGTH = 8;
    private static final int PEER_LENGTH = 28;
    private static final int PEERS_PER_PACKET = 50;

    public OpentrackerLiveSync() {
        new SecureRandom().nextBytes(id);
    }

    public void start() throws IOException {
        channel = DatagramChannel.open(StandardProtocolFamily.INET)
                .setOption(StandardSocketOptions.SO_REUSEADDR, true)
                .bind(new InetSocketAddress(0))
                .setOption(StandardSocketOptions.IP_MULTICAST_IF, NetworkInterface.getByInetAddress(InetAddress.getLocalHost()));
        channel.configureBlocking(false);
        running = true;
        new Thread(this::send).start();
    }

    public void incomingPacket(MessageBase msg) {
        if (msg.isValidPeerRequest()) {
            ByteBuffer buffer = ByteBuffer.allocate(HEADER_LENGTH + PEER_LENGTH);
            buffer.put(id);
            buffer.putInt(msg.getInfoHash());
            buffer.put(msg.getPeerInfo());
            buffer.flip();
            toSend.add(buffer);
        }
    }

    private void send() {
        ByteBuffer packet = ByteBuffer.allocate(HEADER_LENGTH + PEERS_PER_PACKET * PEER_LENGTH);
        while (running) {
            try {
                ByteBuffer peerInfo = toSend.poll();
                if (peerInfo == null) {
                    Thread.sleep(100);
                    continue;
                }

                packet.clear();
                packet.put(id);
                packet.putInt(0); // placeholder for peer count
                int peerCount = 0;

                while (peerInfo != null && peerCount < PEERS_PER_PACKET) {
                    packet.put(peerInfo.array(), HEADER_LENGTH, PEER_LENGTH);
                    peerCount++;
                    peerInfo = toSend.poll();
                }

                if (peerCount > 0) {
                    packet.putInt(4, peerCount);
                    packet.flip();
                    channel.send(packet, new InetSocketAddress("239.192.152.143", 6771));
                }
            } catch (InterruptedException | IOException e) {
                if (running) {
                    e.printStackTrace();
                }
            }
        }
    }

    public void stop() throws IOException {
        running = false;
        if (channel != null) {
            channel.close();
        }
    }
}

