public class RdioScannerHttpClient {
    private final HttpClient mHttpClient;

    public RdioScannerHttpClient() {
        this.mHttpClient = HttpClient.newBuilder()
            .version(HttpClient.Version.HTTP_2)
            .connectTimeout(Duration.ofSeconds(20))
            .followRedirects(HttpClient.Redirect.NORMAL)
            .build();
    }

    public String testConnection(RdioScannerConfiguration configuration) {
        try {
            HttpRequest request = HttpRequest.newBuilder()
                .uri(URI.create(configuration.getBaseUrl() + "/api/test"))
                .header("Content-Type", "application/json")
                .header("X-API-Key", configuration.getApiKey())
                .header("X-System-ID", String.valueOf(configuration.getSystemId()))
                .GET()
                .build();

            HttpResponse<String> response = mHttpClient.send(request, BodyHandlers.ofString());
            return response.statusCode() + ": " + response.body();
        } catch (Exception e) {
            return "Error: " + e.getMessage();
        }
    }

    public void sendAudioRecordingAsync(RdioScannerConfiguration config, RdioScannerBuilder bodyBuilder) {
        HttpRequest request = HttpRequest.newBuilder()
            .uri(URI.create(config.getBaseUrl() + "/api/audio-upload"))
            .header("X-API-Key", config.getApiKey())
            .header("X-System-ID", String.valueOf(config.getSystemId()))
            .POST(bodyBuilder.build())
            .build();

        CompletableFuture<HttpResponse<String>> future = mHttpClient.sendAsync(request, BodyHandlers.ofString());
        future.thenAccept(response -> {
