import org.slf4j.LoggerFactory;

public class KafkaAdminTopicConfigProvider {
    private Properties _clusterConfigs;
    private AdminClient _adminClient;
    private static final Duration DESCRIBE_CLUSTER_CONFIGS_TIMEOUT = Duration.ofSeconds(90);
    private static final Logger LOG = LoggerFactory.getLogger(KafkaAdminTopicConfigProvider.class);

    public Properties topicConfigs(String topic) {
        Properties props = new Properties();
        try {
            ConfigResource resource = new ConfigResource(ConfigResource.Type.TOPIC, topic);
            DescribeConfigsResult result = _adminClient.describeConfigs(Collections.singleton(resource));
            Config config = result.all().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis(), TimeUnit.MILLISECONDS).get(resource);
            props = convertConfigToProperties(config);
        } catch (InterruptedException | ExecutionException | TimeoutException e) {
            LOG.warn("Failed to retrieve configs for topic: " + topic, e);
        }
        return props;
    }

    public Map<String, Properties> topicConfigs(Set<String> topics) {
        Map<String, Properties> configs = new HashMap<>();
        try {
            List<ConfigResource> resources = topics.stream()
                .map(topic -> new ConfigResource(ConfigResource.Type.TOPIC, topic))
                .collect(Collectors.toList());
            DescribeConfigsResult result = _adminClient.describeConfigs(resources);
            Map<ConfigResource, Config> configMap = result.all().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis(), TimeUnit.MILLISECONDS);
            for (Map.Entry<ConfigResource, Config> entry : configMap.entrySet()) {
                configs.put(entry.getKey().name(), convertConfigToProperties(entry.getValue()));
            }
        } catch (InterruptedException | ExecutionException | TimeoutException e) {
            LOG.warn("Failed to retrieve configs for topics: " + topics, e);
        }
        return configs;
    }

    public Map<String, Properties> allTopicConfigs() {
        Map<String, Properties> configs = new HashMap<>();
        try {
            ListTopicsResult topicsResult = _adminClient.listTopics();
            Set<String> topicNames = topicsResult.names().get(DESCRIBE_CLUSTER_CONFIGS_TIMEOUT.toMillis(), TimeUnit.MILLISECONDS);
            configs = topicConfigs(topicNames);
        } catch (InterruptedException | ExecutionException | TimeoutException e) {
            LOG.warn("Failed to retrieve all topic configs", e);
        }
        return configs;
    }

    private Properties convertConfigToProperties(Config config) {
        Properties props = new Properties();
        if (config != null) {
            config.entries().stream()
                .filter(entry -> entry.value() != null)
                .forEach(entry -> props.setProperty(entry.name(), entry.value()));
        }
        return props;
    }
}

