import java.net.URI;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;
import org.java_websocket.client.WebSocketClient;
import org.java_websocket.handshake.ServerHandshake;
import org.json.JSONObject;

public class TransportClient extends WebSocketClient {
    private ExecutorService executorService = Executors.newCachedThreadPool();

    public TransportClient(URI serverUri) {
        super(serverUri);
    }

    @Override
    public void onOpen(ServerHandshake serverHandshake) {
        System.out.println("WebSocket connection established");
    }

    @Override
    public void onMessage(String s) {
        executorService.submit(() -> {
            try {
                JSONObject jsonObject = new JSONObject(s);
                String type = jsonObject.getString("type");
                switch (type) {
                    case "occupy":
                        handleOccupyMessage(jsonObject);
                        break;
                }
            } catch (Exception e) {
                e.printStackTrace();
            }
        });
    }

    @Override
    public void onClose(int i, String s, boolean b) {
        System.out.println("WebSocket connection closed: " + s);
    }

    @Override
    public void onError(Exception e) {
        System.err.println("WebSocket error: " + e.getMessage());
    }

    private void handleOccupyMessage(JSONObject jsonObject) {
        String udId = jsonObject.getString("udId");
        String platform = jsonObject.getString("platform");
        if ("android".equalsIgnoreCase(platform)) {
