                        public void checkClientTrusted(X509Certificate[] certs, String authType) {}
                        public void checkServerTrusted(X509Certificate[] certs, String authType) {}
                    }
                };
                sslContext.init(null, trustAll, new java.security.SecureRandom());
            } catch (Exception e) {
                throw new RuntimeException("Failed to initialize SSL context", e);
            }
        }
    }

    public void initialize(InetSocketAddress address) {
        this.address = address;
        String protocol = usesSSL ? "https" : "http";
        String host = address.getHostString();
        int port = address.getPort();
        this.uri = URI.create(protocol + "://" + host + ":" + port);

        HttpClient.Builder builder = HttpClient.newBuilder()
            .connectTimeout(Duration.ofSeconds(10));
        if (usesSSL) {
            builder.sslContext(sslContext);
        }
        this.httpClient = builder.build();
    }

    public HttpResponse<String> get(String urlPath) throws Exception {
        URI requestUri = uri.resolve(urlPath);
        HttpRequest request = HttpRequest.newBuilder()
            .uri(requestUri)
            .GET()
            .build();
        return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    }

    public HttpResponse<String> post(String urlPath, String body, String[]... headers) throws Exception {
        URI requestUri = uri.resolve(urlPath);
        return postRequest(requestUri, body, headers);
    }

    public HttpResponse<String> post(String body, String[]... headers) throws Exception {
        return postRequest(uri, body, headers);
    }

    private HttpResponse<String> postRequest(URI requestUri, String body, String[]... headers) throws Exception {
        HttpRequest.Builder requestBuilder = HttpRequest.newBuilder()
            .uri(requestUri)
            .POST(HttpRequest.BodyPublishers.ofString(body));
        for (String[] header : headers) {
            if (header.length == 2) {
                requestBuilder.header(header[0], header[1]);
            }
        }
        HttpRequest request = requestBuilder.build();
        return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
    }
}

