import javax.websocket.*;
import javax.websocket.server.ServerEndpoint;
import javax.websocket.server.PathParam;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.logging.Logger;

@ServerEndpoint(value = "/ws/{accessToken}")
public class WebsocketServerEndpoint {
    private static final Logger logger = Logger.getLogger(WebsocketServerEndpoint.class.getName());
    private static final Map<String, MessageHandler> HANDLERS = new ConcurrentHashMap<>();

    @OnOpen
    public void onOpen(Session session, EndpointConfig config, @PathParam("accessToken") String accessToken) {
        try {
            if (!authenticate(accessToken)) {
                session.close(new CloseReason(CloseReason.CloseCodes.VIOLATED_POLICY, "Invalid access token"));
                return;
            }
            logger.info("New client connected: " + session.getId());
        } catch (Exception e) {
            logger.severe("Error during connection opening: " + e.getMessage());
        }
    }

    @OnMessage
    public void onMessage(Session session, String message) {
        try {
            Message msg = Message.fromJson(message);
            MessageHandler handler = HANDLERS.get(msg.getType());
            if (handler != null) {
                handler.handleMessage(session, msg);
            } else {
                logger.warning("No handler found for message type: " + msg.getType());
            }
        } catch (Exception e) {
            logger.severe("Error processing message: " + e.getMessage());
        }
    }

    @OnClose
    public void onClose(Session session, CloseReason closeReason) {
        logger.info("Client disconnected: " + session.getId() + " Reason: " + closeReason);
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.severe("WebSocket error for session " + session.getId() + ": " + throwable.getMessage());
    }

    private Class<? extends Message> getMessageClass(MessageHandler handler) {
        ParameterizedType type = (ParameterizedType) handler.getClass().getGenericInterfaces()[0];
        return (Class<? extends Message>) type.getActualTypeArguments()[0];
    }

    private boolean authenticate(String accessToken) {
        return accessToken != null && !accessToken.isEmpty();
    }
}

