import javax.websocket.*;
import javax.websocket.server.PathParam;
import javax.websocket.server.ServerEndpoint;
import java.io.IOException;
import java.util.Map;
import java.util.concurrent.ConcurrentHashMap;
import java.util.logging.Logger;

@ServerEndpoint(value = "/chat/{username}")
public class ChatRoomServerEndpoint {
    private static final Map<String, Session> ONLINE_USER_SESSIONS = new ConcurrentHashMap<>();
    private Logger logger = Logger.getLogger(this.getClass().getName());

    @OnOpen
    public void openSession(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.put(username, session);
        String message = "User " + username + " joined the chat";
        broadcast(message);
        logger.info(message);
    }

    @OnMessage
    public void onMessage(@PathParam("username") String username, String message) {
        String formattedMessage = username + ": " + message;
        broadcast(formattedMessage);
        logger.info("Message received: " + formattedMessage);
    }

    @OnClose
    public void onClose(@PathParam("username") String username, Session session) {
        ONLINE_USER_SESSIONS.remove(username);
        String message = "User " + username + " left the chat";
        broadcast(message);
        logger.info(message);
    }

    @OnError
    public void onError(Session session, Throwable throwable) {
        logger.severe("Error occurred: " + throwable.getMessage());
        try {
            session.close();
        } catch (IOException e) {
            logger.severe("Error closing session: " + e.getMessage());
        }
    }

    private static void broadcast(String message) {
        ONLINE_USER_SESSIONS.forEach((username, session) -> {
            synchronized (session) {
                try {
                    session.getBasicRemote().sendText(message);
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        });
    }
}

