    public TeenyHttpClient(String baseUri) {
        this.baseUri = baseUri;
        this.httpClient = HttpClient.newBuilder()
                .connectTimeout(Duration.ofSeconds(10))
                .build();
    }

    public HttpResponse<String> get(String url) throws RuntimeException {
        return get(url, new Object[0]);
    }

    public HttpResponse<String> get(String url, Object... params) throws RuntimeException {
        return unchecked(() -> {
            String formattedUrl = String.format(url, params);
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create(baseUri + formattedUrl))
                    .GET()
                    .build();
            return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        });
    }

    public HttpResponse<String> delete(String url, Object... params) throws RuntimeException {
        return unchecked(() -> {
            String formattedUrl = String.format(url, params);
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create(baseUri + formattedUrl))
                    .DELETE()
                    .build();
            return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        });
    }

    public HttpResponse<String> post(String url, Object... params) throws RuntimeException {
        return unchecked(() -> {
            String formattedUrl = String.format(url, params);
            HttpRequest request = HttpRequest.newBuilder()
                    .uri(URI.create(baseUri + formattedUrl))
                    .POST(HttpRequest.BodyPublishers.noBody())
                    .build();
            return httpClient.send(request, HttpResponse.BodyHandlers.ofString());
        });
    }

    private <T> T unchecked(Exceptions.ThrowingSupplier<T> throwingSupplier) {
        try {
            return throwingSupplier.get();
        } catch (Exception e) {
            throw new RuntimeException(e);
        }
    }

    public interface Exceptions {
        @FunctionalInterface
        interface ThrowingSupplier<T> {
            T get() throws Exception;
        }
    }
}

