import io.grpc.ManagedChannel;
import io.grpc.ManagedChannelBuilder;
import io.grpc.health.v1.HealthCheckRequest;
import io.grpc.health.v1.HealthCheckResponse;
import io.grpc.health.v1.HealthGrpc;
import io.grpc.health.v1.HealthGrpc.HealthStub;

public class GrpcHealthClient {
    private final ManagedChannel channel;
    private final HealthStub stub;

    public GrpcHealthClient(String host, int port) {
        this.channel = ManagedChannelBuilder.forAddress(host, port).usePlaintext().build();
        this.stub = HealthGrpc.newStub(channel);
    }

    public HealthCheckResponse checkHealth() throws Exception {
        HealthCheckRequest request = HealthCheckRequest.getDefaultInstance();
        java.util.concurrent.CountDownLatch latch = new java.util.concurrent.CountDownLatch(1);
        final HealthCheckResponse[] response = new HealthCheckResponse[1];
        stub.check(request, new io.grpc.stub.StreamObserver<HealthCheckResponse>() {
            @Override
            public void onNext(HealthCheckResponse value) {
                response[0] = value;
            }

            @Override
            public void onError(Throwable t) {
                latch.countDown();
            }

            @Override
            public void onCompleted() {
                latch.countDown();
            }
        });
        latch.await();
        return response[0];
    }

    public void shutdown() throws InterruptedException {
        channel.shutdown().awaitTermination(5, java.util.concurrent.TimeUnit.SECONDS);
    }
}

