import java.util.List;
import java.util.concurrent.TimeUnit;
import java.util.concurrent.atomic.AtomicBoolean;
import java.util.concurrent.locks.ReentrantReadWriteLock;
import java.util.function.Function;
import io.grpc.ManagedChannel;
import io.grpc.ManagedChannelBuilder;
import io.grpc.StatusRuntimeException;

public class DiscoveryClient {
    private final AtomicBoolean requireResetStub = new AtomicBoolean(false);
    private volatile ManagedChannel channel;
    private volatile DiscoveryServiceBlockingStub registerStub;
    private volatile DiscoveryServiceBlockingStub blockingStub;
    private final ReentrantReadWriteLock readWriteLock = new ReentrantReadWriteLock();
    private final List<String> pdAddresses;

    public DiscoveryClient(String centerAddress) {
        this.pdAddresses = List.of(centerAddress);
        resetChannel(centerAddress);
    }

    public NodeInfos getNodeInfos(Query query) {
        return tryWithTimes((q) -> {
            readWriteLock.readLock().lock();
            try {
                if (requireResetStub.get()) {
                    readWriteLock.readLock().unlock();
                    resetStub();
                    readWriteLock.readLock().lock();
                }
                return blockingStub.getNodeInfos(q);
            } finally {
                readWriteLock.readLock().unlock();
            }
        }, query);
    }

    private <V, R> R tryWithTimes(Function<V, R> function, V v) {
        int retry = 3;
        StatusRuntimeException exception = null;
        while (retry-- > 0) {
            try {
                return function.apply(v);
            } catch (StatusRuntimeException e) {
                exception = e;
                requireResetStub.set(true);
                try {
                    Thread.sleep(100);
                } catch (InterruptedException ignored) {}
            }
        }
        throw exception;
    }

    private void resetStub() {
        readWriteLock.writeLock().lock();
        try {
            if (!requireResetStub.get()) {
                return;
            }
            String address = pdAddresses.get(0);
            resetChannel(address);
            requireResetStub.set(false);
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }

    private void resetChannel(String address) {
        ManagedChannel newChannel = ManagedChannelBuilder.forTarget(address)
            .usePlaintext()
            .build();
        DiscoveryServiceBlockingStub newBlockingStub = DiscoveryServiceGrpc.newBlockingStub(newChannel);
        DiscoveryServiceBlockingStub newRegisterStub = DiscoveryServiceGrpc.newBlockingStub(newChannel);

        ManagedChannel oldChannel = channel;
        channel = newChannel;
        blockingStub = newBlockingStub;
        registerStub = newRegisterStub;

        if (oldChannel != null) {
            oldChannel.shutdown();
        }
    }

    public void close() {
        readWriteLock.writeLock().lock();
        try {
            if (channel != null) {
                channel.shutdown();
                try {
                    channel.awaitTermination(5, TimeUnit.SECONDS);
                } catch (InterruptedException e) {
                    channel.shutdownNow();
                }
            }
        } finally {
            readWriteLock.writeLock().unlock();
        }
    }
}

