        try {
            URL url = new URL(requestUrl);
            if (url.getProtocol().equalsIgnoreCase("https")) {
                HttpsURLConnection httpsConnection = (HttpsURLConnection) url.openConnection();
                SSLContext sslContext = SSLContext.getInstance("SSL");
                sslContext.init(null, new TrustManager[]{new X509TrustUtiil()}, new java.security.SecureRandom());
                httpsConnection.setSSLSocketFactory(sslContext.getSocketFactory());
                httpsConnection.setHostnameVerifier(allHostsValid);
                connection = httpsConnection;
            } else {
                connection = (HttpURLConnection) url.openConnection();
            }

            connection.setConnectTimeout(timeOut > 0 ? timeOut : timeout);
            connection.setReadTimeout(timeOut > 0 ? timeOut : timeout);
            connection.setRequestMethod(requestMethod);
            connection.setDoInput(true);

