    Runtime.getRuntime()
        .addShutdownHook(
            new Thread() {
              @Override
              public void run() {
                System.err.println("*** shutting down gRPC server since JVM is shutting down");
                HelloWorldServer.this.stop();
                System.err.println("*** server shut down");
              }
            });
  }

  public void stop() {
    if (server != null) {
      server.shutdown();
    }
  }

  public void blockUntilShutdown() throws InterruptedException {
    if (server != null) {
      server.awaitTermination();
    }
  }

  static class GreeterImpl extends StreamingGreeterGrpc.StreamingGreeterImplBase {
    @Override
    public void sayHelloStreaming(HelloRequest req, StreamObserver<HelloReply> responseObserver) {
      try {
        logger.info("Saying hi");
        responseObserver.onNext(
            HelloReply.newBuilder().setMessage("Hello " + req.getName()).build());
        Thread.sleep(100);
        logger.info("Saying hi");
        responseObserver.onNext(
            HelloReply.newBuilder().setMessage("Hello " + req.getName() + "!").build());
        Thread.sleep(100);
        logger.info("Saying hi");
        responseObserver.onNext(
