import io.grpc.BindableService;
import io.grpc.MethodDescriptor;
import io.grpc.Server;
import io.grpc.ServerBuilder;
import io.grpc.ServerInterceptor;
import io.grpc.ServerInterceptors;
import io.grpc.ServerServiceDefinition;
import io.grpc.ServiceDescriptor;
import java.lang.reflect.Method;
import java.util.HashMap;
import java.util.List;
import java.util.Map;
import java.util.concurrent.TimeUnit;

public class GrpcServer {
    private Server server;
    private final Map<String, Class<? extends BindableService>> serviceNameMap = new HashMap<>();
    private final Map<String, Method> fullMethodNameMap = new HashMap<>();

    public GrpcServer(GrpcServerProperties properties, List<ServerInterceptor> interceptors, List<BindableService> services) {
        this(ServerBuilder.forPort(properties.getPort()), properties, interceptors, services);
    }

    public GrpcServer(ServerBuilder<?> builder, GrpcServerProperties properties, List<ServerInterceptor> interceptors, List<BindableService> services) {
        for (BindableService service : services) {
            ServerServiceDefinition interceptedService = ServerInterceptors.intercept(service, interceptors);
            builder.addService(interceptedService);
            serviceNameMap.put(service.bindService().getServiceDescriptor().getName(), service.getClass());
            for (MethodDescriptor<?, ?> method : service.bindService().getMethods()) {
                try {
                    Method m = resolve(method, service.getClass());
                    fullMethodNameMap.put(method.getFullMethodName(), m);
                } catch (Exception e) {
                    throw new RuntimeException("Failed to resolve method: " + method.getFullMethodName(), e);
                }
            }
        }
        this.server = builder.build();
    }

    public boolean isRunning() {
        return server != null && !server.isShutdown();
    }

    public int port() {
        return server.getPort();
    }

    public Class<? extends BindableService> findClass(ServiceDescriptor descriptor) {
        return serviceNameMap.get(descriptor.getName());
    }

    public Method findMethod(MethodDescriptor<?, ?> descriptor) {
        return fullMethodNameMap.get(descriptor.getFullMethodName());
    }

    public Method resolve(MethodDescriptor<?, ?> descriptor, Class<? extends BindableService> cls) throws NoSuchMethodException {
        String methodName = descriptor.getBareMethodName();
        Class<?> requestType = descriptor.getRequestMarshaller().getMessageClass();
        Class<?> responseType = descriptor.getResponseMarshaller().getMessageClass();
        return cls.getMethod(methodName, requestType, io.grpc.stub.StreamObserver.class);
    }

    public void onApplicationStart() throws Exception {
        server.start();
        server.awaitTermination();
    }

    public void onApplicationStop() throws Exception {
        if (server != null) {
            server.shutdown().awaitTermination(30, TimeUnit.SECONDS);
        }
    }
}

