import io.grpc.ServerCall;
import io.grpc.ServerCallHandler;
import io.grpc.Status;
import io.grpc.stub.StreamObserver;
import brave.propagation.TraceContext;
import brave.propagation.TraceContextOrSamplingFlags;
import brave.propagation.Propagation;
import brave.propagation.TraceContext.Extractor;
import java.util.Map;
import java.util.concurrent.BlockingQueue;
import java.util.concurrent.LinkedBlockingQueue;
import java.util.concurrent.TimeUnit;

public class TestServer {
  private final BlockingQueue<Long> delayQueue = new LinkedBlockingQueue<>();
  private final BlockingQueue<Metadata> headers = new LinkedBlockingQueue<>();
  private final BlockingQueue<TraceContextOrSamplingFlags> requests = new LinkedBlockingQueue<>();
  private final Extractor<GrpcServerRequest> extractor;
  private Server server;

  public TestServer(Map<String, Key<String>> nameToKey, Propagation<String> propagation) {
    this.extractor = propagation.extractor(GrpcServerRequest::getHeader);
  }

  public void start() throws Exception {
    ServerInterceptor interceptor = new ServerInterceptor() {
      @Override
      public <ReqT, RespT> ServerCall.Listener<ReqT> interceptCall(
          ServerCall<ReqT, RespT> call, Metadata headers, ServerCallHandler<ReqT, RespT> next) {
        TestServer.this.headers.add(headers);
        TraceContextOrSamplingFlags context = extractor.extract(new GrpcServerRequest(headers));
        requests.add(context);
        return new ForwardingServerCallListener.SimpleForwardingServerCallListener<ReqT>(next.startCall(call, headers)) {
          @Override
          public void onMessage(ReqT message) {
            try {
              Long delay = delayQueue.poll();
              if (delay != null) {
                Thread.sleep(delay);
              }
            } catch (InterruptedException e) {
              Thread.currentThread().interrupt();
              call.close(Status.CANCELLED.withDescription("Interrupted"), new Metadata());
            }
            super.onMessage(message);
          }
        };
      }
    };

    server = ServerBuilder.forPort(0)
        .addService(new TestServiceImpl())
        .intercept(interceptor)
        .build()
        .start();
  }

  public void stop() throws InterruptedException {
    if (server != null) {
      server.shutdown();
      server.awaitTermination(3, TimeUnit.SECONDS);
    }
  }

  public int port() {
    return server.getPort();
  }

  public TraceContextOrSamplingFlags takeRequest() throws InterruptedException {
    return requests.poll(3, TimeUnit.SECONDS);
  }

  public void enqueueDelay(long millis) {
    delayQueue.add(millis);
  }

  private static class GrpcServerRequest {
    private final Metadata headers;

    GrpcServerRequest(Metadata headers) {
      this.headers = headers;
    }

    public String getHeader(String name) {
      return headers.get(Metadata.Key.of(name, Metadata.ASCII_STRING_MARSHALLER));
    }
  }

  private static class TestServiceImpl extends TestServiceGrpc.TestServiceImplBase {
    @Override
    public void test(TestRequest request, StreamObserver<TestResponse> responseObserver) {
