import lombok.extern.slf4j.Slf4j;
import java.io.IOException;
import java.util.Map;
import java.util.concurrent.TimeUnit;

@Slf4j
@Component
public class GrpcLauncher {
    private Server server;
    @Value("${grpc.server.port}")
    private int grpcServerPort;
    private static final int DEFAULT_MAX_MESSAGE_SIZE = 10 * 1024 * 1024;

    public void grpcStart(Map<String, Object> grpcServiceBeanMap) {
        ServerBuilder<?> serverBuilder = ServerBuilder.forPort(grpcServerPort)
                .maxInboundMessageSize(DEFAULT_MAX_MESSAGE_SIZE);

        grpcServiceBeanMap.values().forEach(bean -> {
            serverBuilder.addService(bean);
            log.info("Registered gRPC service: {}", bean.getClass().getName());
        });

        try {
            server = serverBuilder.build().start();
            log.info("gRPC server started, listening on port {}", grpcServerPort);

            Runtime.getRuntime().addShutdownHook(new Thread(() -> {
                log.info("Shutting down gRPC server since JVM is shutting down");
                grpcStop();
                log.info("gRPC server shut down");
            }));
        } catch (IOException e) {
            log.error("Failed to start gRPC server", e);
            throw new RuntimeException("Failed to start gRPC server", e);
        }
    }

    public void grpcStop() {
        if (server != null) {
            try {
                server.shutdown().awaitTermination(30, TimeUnit.SECONDS);
            } catch (InterruptedException e) {
                log.error("gRPC server shutdown interrupted", e);
                Thread.currentThread().interrupt();
            }
        }
    }
}

