import io.prometheus.client.Counter;
import io.prometheus.client.exporter.HTTPServer;
import io.prometheus.client.CollectorRegistry;
import org.slf4j.Logger;
import org.slf4j.LoggerFactory;
import java.io.IOException;
import java.util.ArrayList;
import java.util.List;

public class DataHandler {
    private static final Logger LOG = LoggerFactory.getLogger(DataHandler.class);
    private static final int PORT = 1111;
    private static final Counter syncRequests = Counter.build()
            .name("sync_requests_total")
            .help("Total synchronous requests.")
            .register();
    private static final Counter streamingRequests = Counter.build()
            .name("streaming_requests_total")
            .help("Total streaming requests.")
            .register();
    private Server grpcServer;
    private HTTPServer prometheusHttpServer;

    public void start() throws IOException {
        CollectorRegistry registry = new CollectorRegistry();
        prometheusHttpServer = new HTTPServer.Builder()
                .withPort(9090)
                .withRegistry(registry)
                .build();
        grpcServer = ServerBuilder.forPort(PORT)
                .addService(new DataImpl())
                .intercept(new PrometheusServerInterceptor(registry))
                .build()
                .start();
        LOG.info("Server started, listening on {}", PORT);
        Runtime.getRuntime().addShutdownHook(new Thread(() -> {
            LOG.info("Shutting down gRPC server");
            DataHandler.this.stop();
            LOG.info("Server shut down");
        }));
    }

    public void blockUntilShutdown() throws InterruptedException {
        if (grpcServer != null) {
            grpcServer.awaitTermination();
        }
    }

    public void stop() {
        if (grpcServer != null) {
            grpcServer.shutdown();
        }
        if (prometheusHttpServer != null) {
            prometheusHttpServer.stop();
        }
    }

    static class DataImpl extends DataServiceGrpc.DataServiceImplBase {
        @Override
        public void get(DataRequest request, StreamObserver<DataResponse> responseObserver) {
            syncRequests.inc();
            DataResponse response = DataResponse.newBuilder()
                    .setData(request.getData().toUpperCase())
                    .build();
            responseObserver.onNext(response);
            responseObserver.onCompleted();
        }

        @Override
        public void streamingGet(EmptyRequest request, StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            for (int i = 0; i < 10; i++) {
                DataResponse response = DataResponse.newBuilder()
                        .setData("Response " + i)
                        .build();
                responseObserver.onNext(response);
            }
            responseObserver.onCompleted();
        }

        @Override
        public StreamObserver<DataRequest> streamingPut(StreamObserver<DataResponse> responseObserver) {
            streamingRequests.inc();
            List<String> collectedData = new ArrayList<>();
            return new StreamObserver<DataRequest>() {
                @Override
                public void onNext(DataRequest request) {
                    collectedData.add(request.getData().toUpperCase());
                }

                @Override
                public void onError(Throwable t) {
                    LOG.error("Error in streaming", t);
                }

                @Override
                public void onCompleted() {
                    DataResponse response = DataResponse.newBuilder()
                            .setData(String.join(",", collectedData))
                            .build();
                    responseObserver.onNext(response);
                    responseObserver.onCompleted();
                }
            };
        }
    }
}

