import java.io.*;
import java.net.*;
import java.util.*;

public class TCPServer {
    private boolean mIsServiceDestroyed = false;
    private String[] mDefinedMessages = {
        "Hello!",
        "How are you?",
        "Nice to meet you!",
        "What's up?",
        "Goodbye!"
    };
    private ServerSocket mServerSocket;
    private List<Thread> mClientThreads = new ArrayList<>();

    public void startServer(int port) {
        try {
            mServerSocket = new ServerSocket(port);
            Thread serverThread = new Thread(new TcpServer());
            serverThread.start();
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void stopServer() {
        mIsServiceDestroyed = true;
        try {
            if (mServerSocket != null) {
                mServerSocket.close();
            }
            for (Thread thread : mClientThreads) {
                if (thread != null && thread.isAlive()) {
                    thread.interrupt();
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private class TcpServer implements Runnable {
        @Override
        public void run() {
            while (!mIsServiceDestroyed) {
                try {
                    Socket client = mServerSocket.accept();
                    Thread clientThread = new Thread(() -> responseClient(client));
                    mClientThreads.add(clientThread);
                    clientThread.start();
                } catch (SocketException e) {
                    if (!mIsServiceDestroyed) {
                        e.printStackTrace();
                    }
                } catch (IOException e) {
                    e.printStackTrace();
                }
            }
        }
    }

    private void responseClient(Socket client) {
        try (BufferedReader in = new BufferedReader(new InputStreamReader(client.getInputStream()));
             PrintWriter out = new PrintWriter(client.getOutputStream(), true)) {
            Random random = new Random();
            String inputLine;
            while ((inputLine = in.readLine()) != null) {
                String response = mDefinedMessages[random.nextInt(mDefinedMessages.length)];
                out.println(response);
            }
        } catch (IOException e) {
            if (!mIsServiceDestroyed) {
                e.printStackTrace();
            }
        } finally {
            try {
                client.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }
}

