import java.io.ByteArrayOutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.HttpURLConnection;
import java.net.URL;
import org.bouncycastle.cert.ocsp.OCSPReq;
import org.bouncycastle.cert.ocsp.OCSPResp;

public class OCSPClient {
    public static OCSPResp sendOCSPRequest(String url, OCSPReq ocspReq) throws IOException {
        HttpURLConnection connection = null;
        try {
            URL ocspUrl = new URL(url);
            connection = (HttpURLConnection) ocspUrl.openConnection();
            connection.setRequestMethod("POST");
            connection.setRequestProperty("Content-Type", "application/ocsp-request");
            connection.setRequestProperty("Accept", "application/ocsp-response");
            connection.setDoOutput(true);

            try (OutputStream out = connection.getOutputStream()) {
                out.write(ocspReq.getEncoded());
            }

            int responseCode = connection.getResponseCode();
            if (responseCode != HttpURLConnection.HTTP_OK) {
                throw new IOException("HTTP error code: " + responseCode);
            }

            ByteArrayOutputStream responseBuffer = new ByteArrayOutputStream();
            try (InputStream in = connection.getInputStream()) {
                byte[] buffer = new byte[4096];
                int bytesRead;
                while ((bytesRead = in.read(buffer)) != -1) {
                    responseBuffer.write(buffer, 0, bytesRead);
                }
            }

            return new OCSPResp(responseBuffer.toByteArray());
        } finally {
            if (connection != null) {
                connection.disconnect();
            }
        }
    }
}

