import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetAddress;
import java.net.ServerSocket;
import java.net.Socket;
import java.net.SocketException;
import java.net.UnknownHostException;

public class TcpTransport {
    private ServerSocket srv_sock;
    private Socket sock;
    private OutputStream output;
    private InputStream input;
    private Thread receiver_thread;
    private RtReceiver receiver;
    private InetAddress host;
    private int port = 7800;
    private int out_buf_size = 8192;
    private int in_buf_size = 8192;
    private boolean server;
    private boolean tcp_nodelay;
    private Log log;

    public interface RtReceiver {
        void onReceive(byte[] data, int length);
    }

    public TcpTransport(RtReceiver receiver, Log log) {
        this.receiver = receiver;
        this.log = log;
    }

    public void start(String... options) throws IOException {
        for (String option : options) {
            String[] parts = option.split("=");
            switch (parts[0]) {
                case "host":
                    try {
                        host = InetAddress.getByName(parts[1]);
                    } catch (UnknownHostException e) {
                        throw new IOException("Invalid host: " + parts[1], e);
                    }
                    break;
                case "port":
                    port = Integer.parseInt(parts[1]);
                    break;
                case "out_buf_size":
                    out_buf_size = Integer.parseInt(parts[1]);
                    break;
                case "in_buf_size":
                    in_buf_size = Integer.parseInt(parts[1]);
                    break;
                case "server":
                    server = Boolean.parseBoolean(parts[1]);
                    break;
                case "tcp_nodelay":
                    tcp_nodelay = Boolean.parseBoolean(parts[1]);
                    break;
            }
        }

        if (server) {
            srv_sock = new ServerSocket(port);
            sock = srv_sock.accept();
        } else {
            if (host == null) {
                throw new IOException("Host must be specified in client mode");
            }
            sock = new Socket(host, port);
        }

        sock.setTcpNoDelay(tcp_nodelay);
        output = sock.getOutputStream();
        input = sock.getInputStream();

        receiver_thread = new Thread(new Receiver(input));
        receiver_thread.start();
    }

    public void stop() {
        try {
            if (receiver_thread != null) {
                receiver_thread.interrupt();
                receiver_thread.join();
            }
            if (input != null) input.close();
            if (output != null) output.close();
            if (sock != null) sock.close();
            if (srv_sock != null) srv_sock.close();
        } catch (IOException | InterruptedException e) {
            log.error("Error stopping transport: " + e.getMessage());
        }
    }

    public void send(byte[] buf, int offset, int length) throws IOException {
        output.write(buf, offset, length);
        output.flush();
    }

    private class Receiver implements Runnable {
        private InputStream in;

        public Receiver(InputStream in) {
            this.in = in;
        }

        @Override
        public void run() {
            byte[] buffer = new byte[in_buf_size];
            try {
                while (!Thread.currentThread().isInterrupted()) {
                    int bytesRead = in.read(buffer);
                    if (bytesRead == -1) break;
                    receiver.onReceive(buffer, bytesRead);
                }
            } catch (SocketException e) {
                if (!Thread.currentThread().isInterrupted()) {
                    log.error("Receiver thread socket error: " + e.getMessage());
                }
            } catch (IOException e) {
                log.error("Receiver thread error: " + e.getMessage());
            }
        }
    }
}

