import java.io.*;
import java.net.*;
import java.util.concurrent.ExecutorService;
import java.util.concurrent.Executors;

public class HttpMediaStreamer {
    private Uri uri;
    private ServerSocket serverSocket;
    private static final String TAG = "HttpMediaStreamer";
    private final String filename;
    private final String mimeType;
    private ExecutorService executorService;

    public HttpMediaStreamer(String filename, String mimeType) {
        this.filename = filename;
        this.mimeType = mimeType;
        this.uri = create(filename, mimeType);
    }

    public Uri getUri() {
        return uri;
    }

    public void destroy() {
        try {
            if (serverSocket != null && !serverSocket.isClosed()) {
                serverSocket.close();
            }
            if (executorService != null) {
                executorService.shutdownNow();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    private Uri create(String filename, String mimeType) {
        try {
            serverSocket = new ServerSocket(0);
            int port = serverSocket.getLocalPort();
            uri = Uri.parse("http://127.0.0.1:" + port);

            executorService = Executors.newSingleThreadExecutor();
            executorService.submit(() -> {
                try {
                    while (!serverSocket.isClosed()) {
                        Socket clientSocket = serverSocket.accept();
                        new Thread(() -> handleClient(clientSocket, filename, mimeType)).start();
                    }
                } catch (IOException e) {
                    if (!serverSocket.isClosed()) {
                        e.printStackTrace();
                    }
                }
            });

            return uri;
        } catch (IOException e) {
            throw new RuntimeException("Failed to start HTTP server", e);
        }
    }

    private void handleClient(Socket clientSocket, String filename, String mimeType) {
        try (BufferedReader in = new BufferedReader(new InputStreamReader(clientSocket.getInputStream()));
             OutputStream out = clientSocket.getOutputStream()) {

            String request = in.readLine();
            if (request == null || !request.startsWith("GET ")) {
                sendErrorResponse(out, 400, "Bad Request");
                return;
            }

            File file = new File(filename);
            if (!file.exists()) {
                sendErrorResponse(out, 404, "Not Found");
                return;
            }

            String responseHeaders = "HTTP/1.1 200 OK\r\n" +
                    "Content-Type: " + mimeType + "\r\n" +
                    "Content-Length: " + file.length() + "\r\n" +
                    "Connection: close\r\n\r\n";
            out.write(responseHeaders.getBytes());

            try (FileInputStream fis = new FileInputStream(file)) {
                byte[] buffer = new byte[8192];
                int bytesRead;
                while ((bytesRead = fis.read(buffer)) != -1) {
                    out.write(buffer, 0, bytesRead);
                }
            }
        } catch (IOException e) {
            e.printStackTrace();
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                e.printStackTrace();
            }
        }
    }

    private void sendErrorResponse(OutputStream out, int statusCode, String statusMessage) throws IOException {
        String response = "HTTP/1.1 " + statusCode + " " + statusMessage + "\r\n\r\n";
        out.write(response.getBytes());
    }

    public static class Uri {
        private final String uri;

        private Uri(String uri) {
            this.uri = uri;
        }

        public static Uri parse(String uri) {
            return new Uri(uri);
        }

        @Override
        public String toString() {
            return uri;
        }
    }
}

