public class EchoServer extends Thread {
    private final int port;
    private ServerSocket serverSocket;
    private final List<Thread> threads = new CopyOnWriteArrayList<>();
    private final List<Socket> sockets = new CopyOnWriteArrayList<>();
    private final List<Exception> exceptions = new CopyOnWriteArrayList<>();
    private final AtomicBoolean renegotiate = new AtomicBoolean(false);
    private final boolean sslEnabled;
    private final SSLServerSocketFactory sslFactory;

    public EchoServer(int port) {
        this.port = port;
        this.sslEnabled = false;
        this.sslFactory = null;
    }

    public EchoServer(SSLServerSocketFactory sslFactory, int port) {
        this.port = port;
        this.sslEnabled = true;
        this.sslFactory = sslFactory;
    }

    public void renegotiate() {
        renegotiate.set(true);
    }

    @Override
    public void run() {
        try {
            if (sslEnabled) {
                serverSocket = sslFactory.createServerSocket(port);
            } else {
                serverSocket = new ServerSocket(port);
            }

            while (!serverSocket.isClosed()) {
                Socket clientSocket = serverSocket.accept();
                sockets.add(clientSocket);
                Thread clientThread = new Thread(() -> handleClient(clientSocket));
                threads.add(clientThread);
                clientThread.start();
            }
        } catch (IOException e) {
            if (!serverSocket.isClosed()) {
                exceptions.add(e);
            }
        }
    }

    private void handleClient(Socket clientSocket) {
        try (InputStream input = clientSocket.getInputStream();
             OutputStream output = clientSocket.getOutputStream()) {
            byte[] buffer = new byte[1024];
            int bytesRead;

            while (!clientSocket.isClosed()) {
                if (sslEnabled && renegotiate.get()) {
                    ((SSLSocket) clientSocket).startHandshake();
                }

