                if (bytesRead == -1) {
                    break;
                }
                output.write(buffer, 0, bytesRead);
                output.flush();
            }
        } catch (Exception e) {
            exceptions.add(e);
        } finally {
            try {
                clientSocket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
            sockets.remove(clientSocket);
            threads.remove(Thread.currentThread());
        }
    }

    public void closeConnections() {
        for (Socket socket : sockets) {
            try {
                socket.close();
            } catch (IOException e) {
                exceptions.add(e);
            }
        }
        sockets.clear();
    }

    public void close() {
        try {
            serverSocket.close();
        } catch (IOException e) {
            exceptions.add(e);
        }
        closeConnections();
        for (Thread thread : threads) {
            try {
                thread.join();
            } catch (InterruptedException e) {
                exceptions.add(e);
            }
        }
        threads.clear();
    }

    public int getExceptionCount() {
        return exceptions.size();
    }
}

