                }
            }

            GSSCredential server = new GSSCredential(new GSSName(serviceName,
                GSSName.NT_HOSTBASED_SERVICE), GSSCredential.INDEFINITE,
                mechOid, GSSCredential.ACCEPT_ONLY);

